public func getGPSData(city: String) -> (long: Double, lat: Double) {
	var longitude: [String: Double] = [:]
	var latitude: [String: Double] = [:]
	for element in gpsByCity {
		let longChars = element[1].characters
		let longdegRange = longChars.startIndex...longChars.startIndex.advancedBy(2)
		let longminRange = longChars.startIndex.advancedBy(4)...longChars.startIndex.advancedBy(5)
		let longsecRange = longChars.startIndex.advancedBy(7)...longChars.startIndex.advancedBy(8)
		let longdeg = String(longChars[longdegRange])
		let longmin = String(longChars[longminRange])
		let longsec = String(longChars[longsecRange])
		let longX = ((Double(longdeg)! + (Double(longmin)! / 60.0) + (Double(longsec)! / 3600.0)) * 1000000.0)
		let long = round(longX) / 1000000.0
		longitude[element[0]] = long
		
		let latChars = element[2].characters
		let latdegRange = latChars.startIndex...latChars.startIndex.advancedBy(1)
		let latminRange = latChars.startIndex.advancedBy(3)...latChars.startIndex.advancedBy(4)
		let latsecRange = latChars.startIndex.advancedBy(6)...latChars.startIndex.advancedBy(7)
		let latdeg = String(latChars[latdegRange])
		let latmin = String(latChars[latminRange])
		let latsec = String(latChars[latsecRange])
		let latX = ((Double(latdeg)! + (Double(latmin)! / 60.0) + (Double(latsec)! / 3600.0)) * 1000000.0)
		let lat = round(latX) / 1000000.0
		latitude[element[0]] = lat
	}
	if let long = longitude[city] {
		if let lat = latitude[city] {
			return (long, lat)
		}
	}
	return (0.0, 0.0)
}

let EARTH_RADIUS = 6378137.0
let PI = M_PI

public func getDistance(lat1: Double, long1: Double, lat2: Double, long2: Double) -> Double {
	func convertDegreeToRadians(degrees: Double) -> Double {
		return (PI / 180) * degrees
	}
	let lat1r = convertDegreeToRadians(lat1)
	let lat2r = convertDegreeToRadians(lat2)
	let long1r = convertDegreeToRadians(long1)
	let long2r = convertDegreeToRadians(long2)
	let R = EARTH_RADIUS // Earth's radius (km)
	let d = acos(sin(lat1r) * sin(lat2r) + cos(lat1r) * cos(lat2r) * cos(long2r - long1r)) * R
	return d
}

public func distance(from: String, to : String) -> Double {
	let lat_a = getGPSData(from).lat
	let long_a = getGPSData(from).long
	let lat_b = getGPSData(to).lat
	let long_b = getGPSData(to).long
	return getDistance(lat_a, long1: long_a, lat2: lat_b, long2: long_b)
}
